import { UserRole, CompanyType } from "@prisma/client";
import "next-auth";
import "next-auth/jwt";

declare module "next-auth" {
  interface Session {
    user: {
      id: string;
      email: string;
      name?: string | null;
      role: UserRole;
      companyId: string;
      companyType: CompanyType;
    };
  }

  interface User {
    role: UserRole;
    companyId: string;
    companyType: CompanyType;
  }
}

declare module "next-auth/jwt" {
  interface JWT {
    id: string;
    role: UserRole;
    companyId: string;
    companyType: CompanyType;
  }
}
